import { Component, ViewChild, inject } from "@angular/core";
import { NgForm } from "@angular/forms";
import { AuthService } from "../Services/auth.service";
import { Observable } from "rxjs";
import { AuthResponse } from "../Model/AuthResponse";
import { Router } from "@angular/router";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.css"],
})
export class LoginComponent {
  @ViewChild("authForm") authForm: NgForm;
  authService: AuthService = inject(AuthService);
  router: Router = inject(Router);
  isLoginMode: boolean = true;
  isLoading: boolean = false;
  errorMessage: string | null = null;
  isSubmitted: boolean = false;
  authObs: Observable<AuthResponse>;
  email: string = "";
  password: string = "";

  onSwitchMode() {
    this.isLoginMode = !this.isLoginMode;
  }

  onFormSubmitted(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const email = form.value.email;
    const password = form.value.password;

    this.setLoading(true);

    if (this.isLoginMode) {
      this.authObs = this.authService.login(email, password);
    } else {
      this.authObs = this.authService.signup(email, password);
    }

    this.authObs.subscribe({
      next: (res) => {
        this.onSuccess();
      },
      error: (errMsg) => {
        this.onError(errMsg);
      },
    });
    form.reset();
  }

  canExit() {
    if ((this.email || this.password) && !this.isSubmitted) {
      return confirm("You have unsaved changes. Do you want to navigate away?");
    } else {
      return true;
    }
  }

  hideSnackbar() {
    setTimeout(() => {
      this.errorMessage = null;
    }, 3000);
  }

  private setLoading(isLoading: boolean) {
    this.isLoading = isLoading;
  }

  private onSuccess() {
    this.isSubmitted = true;
    this.setLoading(false);
    this.router.navigate(["/dashboard/overview"]);
  }

  private onError(errMsg) {
    this.setLoading(false);
    this.errorMessage = errMsg;
    this.hideSnackbar();
  }
}
<div class="app-login-form">
  <app-snackbar
    [errorMessage]="errorMessage"
    *ngIf="errorMessage"
  ></app-snackbar>

  <h2>{{ isLoginMode ? "Login" : "Signup" }} Here</h2>

  <app-loader *ngIf="isLoading"></app-loader>

  <form
    #authForm="ngForm"
    (ngSubmit)="onFormSubmitted(authForm)"
    *ngIf="!isLoading"
  >
    <div class="form-input">
      <label>Email Address</label>
      <input
        type="email"
        placeholder="Email address"
        name="email"
        [(ngModel)]="email"
        required
        email
      />
    </div>
    <div class="form-input">
      <label>Password</label>
      <input
        type="password"
        placeholder="Password"
        name="password"
        [(ngModel)]="password"
        required
        minlength="8"
      />
    </div>
    <button type="submit" class="app-login-btn" [disabled]="authForm.invalid">
      {{ isLoginMode ? "Login" : "Signup" }}
    </button>
    <p>
      {{
        isLoginMode ? "Do not have an accout yet?" : "Already have an account?"
      }}
      <button class="app-toggle-btn" type="button" (click)="onSwitchMode()">
        {{ isLoginMode ? "Register Here." : "Login Here" }}
      </button>
    </p>
  </form>
</div>
