import { Component, OnDestroy, OnInit, inject } from "@angular/core";
import { AuthService } from "../Services/auth.service";
import { User } from "../Model/User";
import { Subscription } from "rxjs";

@Component({
  selector: "app-header",
  templateUrl: "./header.component.html",
  styleUrls: ["./header.component.css"],
})
export class HeaderComponent implements OnInit, OnDestroy {
  authService: AuthService = inject(AuthService);
  isLoggedIn: boolean = false;
  isMenuOpen: boolean = false;
  private userSubject: Subscription;

  ngOnInit() {
    this.userSubject = this.authService.user.subscribe((user: User) => {
      this.isLoggedIn = user ? true : false;
    });
  }

  onLogout() {
    this.authService.logout();
  }

  ngOnDestroy() {
    this.userSubject.unsubscribe();
  }

  OnOpenMenu() {
    this.isMenuOpen = true;
  }
  OnCloseMenu() {
    this.isMenuOpen = false;
  }
}
<div class="app-header">
  <!--APP LOGO-->
  <div class="app-header-logo">
    <h2>Task Management</h2>
  </div>

  <!--APP NAVBAR-->
  <div class="app-header-navbar" [ngClass]="{ open: isMenuOpen }">
    <a routerLink="/">HOME</a>

    <div class="dropdown" *ngIf="isLoggedIn; else loginButton">
      <button class="dropbtn">
        DASHBOARD
        <i class="fa fa-caret-down"></i>
      </button>
      <div class="dropdown-content">
        <a routerLink="dashboard/overview">OVERVIEW</a>
        <a routerLink="dashboard/stats">STATS</a>
      </div>
    </div>

    <ng-template #loginButton>
      <a routerLink="/login" *ngIf="!isLoggedIn">LOGIN</a>
    </ng-template>

    <a *ngIf="isLoggedIn" (click)="onLogout()">LOGOUT</a>

    <button class="menu-close-icon" href="#" (click)="OnCloseMenu()">
      &times;
    </button>
  </div>

  <i class="fas fa-bars" (click)="OnOpenMenu()"></i>
</div>
