  (EmitTaskData)="CreateOrUpdateTask($event)"
  [isEditMode]="editMode"
  [selectedTask]="selectedTask"
>
</app-create-task>

<app-task-details
  *ngIf="showTaskDetails"
  (CloseDetailView)="CloseTaskDetails()"
  [currentTask]="currentTask"
></app-task-details>

<div class="dashboard-container">
  <div class="dashboard-header">
    <div style="flex-basis: 50%">
      <h2>Dashboard</h2>
    </div>
    <div style="flex-basis: 50%; text-align: right">
      <button class="create-task-btn" (click)="OpenCreateTaskForm()">
        Create Task
      </button>
      <button class="create-task-btn" (click)="FetchAllTaskClicked()">
        Fetch Tasks
      </button>
      <button class="create-task-btn" (click)="DeleteAllTask()">
        Clear Tasks
      </button>
    </div>
  </div>

  <hr />

  <div class="dashboard-item-container">
    <app-snackbar
      [errorMessage]="errorMessage"
      *ngIf="errorMessage"
    ></app-snackbar>
    <app-loader *ngIf="isLoading"></app-loader>

    <div style="text-align: center" *ngIf="allTasks.length < 1">
      <h2>No task has been created yet!</h2>
    </div>

    <ng-container *ngIf="!isLoading">
      <div
        class="task-status-container"
        *ngFor="let status of statuses"
        setTaskBackground
        [status]="status"
      >
        <div class="task-status-header">
          <span class="number-of-task">{{ tasksByStatus[status].length }}</span>
          <h3>{{ status | uppercase }}</h3>
        </div>

        <div class="task-item" *ngFor="let task of tasksByStatus[status] || []">
          <div class="task-title">
            <h3>{{ task.title }}</h3>
          </div>
          <p><b>Assigned to: </b>{{ task.assignedTo }}</p>
          <p><b>Priority: </b>{{ task.priority }}</p>
          <p><b>Status: </b>{{ task.status }}</p>
          <div class="task-action-icons">
            <button
              class="task-action-btn"
              title="Detail"
              (click)="showCurrentTaskDetails(task.id)"
            >
              <i class="fa fa-info-circle" aria-hidden="true"></i>
            </button>
            <button
              class="task-action-btn"
              title="Edit"
              (click)="OnEditTaskClicked(task.id)"
            >
              <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
            </button>
            <button
              class="task-action-btn"
              title="Delete"
              (click)="DeleteTask(task.id)"
            >
              <i class="fa fa-trash" aria-hidden="true"></i>
            </button>
          </div>
        </div>
        <button
          class="create-task-btn in-table"
          (click)="FetchAllTaskClicked()"
        >
          Add new task
        </button>
      </div>
    </ng-container>
  </div>
</div>
