import { Component, OnInit, inject } from "@angular/core";
import { Task } from "src/app/Model/Task";
import { TaskService } from "src/app/Services/task.service";

@Component({
  selector: "app-stats",
  templateUrl: "./stats.component.html",
  styleUrls: ["./stats.component.css"],
})
export class StatsComponent implements OnInit {
  open: number = 0;
  started: number = 0;
  inprogress: number = 0;
  complete: number = 0;
  total: number = 0;
  tasks: Task[] = [];

  taskService: TaskService = inject(TaskService);

  ngOnInit(): void {
    this.taskService.GetAlltasks().subscribe((tasks: Task[]) => {
      this.tasks = tasks;
      this.open = tasks.filter((task) => task.status === "open").length;
      this.started = tasks.filter((task) => task.status === "started").length;
      this.inprogress = tasks.filter(
        (task) => task.status === "in-progress"
      ).length;
      this.complete = tasks.filter((task) => task.status === "complete").length;
      this.total = tasks.length;
    });
  }
}
<div class="status-content">
  <h2>Task Status & Percentage</h2>
  <table>
    <thead>
      <td>Status</td>
      <td>Count</td>
      <td>Percentage</td>
    </thead>
    <tr>
      <td>Open</td>
      <td>{{ open }}</td>
      <td>{{ ((open / total) * 100).toFixed(2) }} %</td>
    </tr>
    <tr>
      <td>Started</td>
      <td>{{ started }}</td>
      <td>{{ ((started / total) * 100).toFixed(2) }} %</td>
    </tr>
    <tr>
      <td>In Progress</td>
      <td>{{ inprogress }}</td>
      <td>{{ ((inprogress / total) * 100).toFixed(2) }} %</td>
    </tr>
    <tr>
      <td>Closed</td>
      <td>{{ complete }}</td>
      <td>{{ ((complete / total) * 100).toFixed(2) }} %</td>
    </tr>
  </table>
</div>
