import {
  Component,
  EventEmitter,
  Output,
  Input,
  ViewChild,
} from "@angular/core";
import { NgForm } from "@angular/forms";
import { Task } from "src/app/Model/Task";

@Component({
  selector: "app-create-task",
  templateUrl: "./create-task.component.html",
  styleUrls: ["./create-task.component.css"],
})
export class CreateTaskComponent {
  @Input() isEditMode: boolean = false;
  @Input() selectedTask: Task;
  @ViewChild("taskForm") taskForm: NgForm;
  @Output()
  CloseForm: EventEmitter<boolean> = new EventEmitter<boolean>();
  @Output()
  EmitTaskData: EventEmitter<Task> = new EventEmitter<Task>();

  ngAfterViewInit() {
    setTimeout(() => {
      this.taskForm.form.patchValue(this.selectedTask);
    }, 0);
  }

  OnCloseForm() {
    this.CloseForm.emit(false);
  }

  OnFormSubmitted(form: NgForm) {
    console.log(form);
    // this.EmitTaskData.emit(form.value);
    // this.OnCloseForm();
  }
}
<div class="overlay">
  <div class="popup">
    <h2>{{ isEditMode ? "Update Task" : "Create a new Task" }}</h2>
    <a class="close" href="#" (click)="OnCloseForm()">&times;</a>
    <div class="content">
      <section class="container">
        <form
          action="#"
          class="form"
          #taskForm="ngForm"
          (ngSubmit)="OnFormSubmitted(taskForm)"
        >
          <div class="input-box">
            <label>Title*</label>
            <input type="text" required name="title" ngModel />
          </div>
          <div class="input-box">
            <label>Description*</label>
            <textarea rows="8" name="desc" required ngModel></textarea>
          </div>
          <div class="column">
            <div class="input-box">
              <label>Assigned To</label>
              <input type="text" name="assignedTo" required ngModel />
            </div>
            <div class="input-box">
              <label>Created At</label>
              <input type="date" name="createdAt" required ngModel />
            </div>
          </div>
          <div class="input-box address">
            <div class="column">
              <div class="select-box">
                <select name="priority" ngModel>
                  <option hidden>Priority</option>
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="critical">Critical</option>
                </select>
              </div>
              <div class="select-box">
                <select name="status" ngModel>
                  <option hidden>Status</option>
                  <option value="open">Open</option>
                  <option value="started">Started</option>
                  <option value="in-progress">In Progress</option>
                  <option value="complete">Complete</option>
                </select>
              </div>
            </div>
          </div>
          <button type="submit">
            {{ isEditMode ? "Update Task" : "Create Task" }}
          </button>
        </form>
      </section>
    </div>
  </div>
</div>
