import { Component, OnInit, inject } from "@angular/core";
import { Task } from "../../Model/Task";
import {
  HttpClient,
  HttpHeaders,
  HttpErrorResponse,
} from "@angular/common/http";
import { TaskService } from "../../Services/task.service";
import { Subscription } from "rxjs";
import { NgForm } from "@angular/forms";

@Component({
  selector: "app-overview",
  templateUrl: "./overview.component.html",
  styleUrls: ["./overview.component.css"],
})
export class OverviewComponent implements OnInit {
  http: HttpClient = inject(HttpClient);
  taskService: TaskService = inject(TaskService);
  showCreateTaskForm: boolean = false;
  showTaskDetails: boolean = false;
  allTasks: Task[] = [];
  selectedTask: Task;
  currentTask: Task | null = null;
  currentTaskId: string = "";
  searchedTasks: Task[] = [];
  searchQuery: string = "";
  isLoading: boolean = false;
  errorMessage: string | null = null;
  editMode: boolean = false;
  errorSub: Subscription;

  statuses = ["open", "started", "in-progress", "complete"];

  ngOnInit() {
    this.fetchAllTasks();
    this.errorSub = this.taskService.errorSubject.subscribe({
      next: (httpError) => {
        this.setErrorMessage(httpError);
      },
    });
  }

  ngOnDestroy() {
    this.errorSub.unsubscribe();
  }

  OpenCreateTaskForm() {
    this.showCreateTaskForm = true;
    this.editMode = false;
    this.selectedTask = {
      title: "",
      desc: "",
      assignedTo: "",
      createdAt: "",
      priority: "",
      status: "",
    };
  }

  showCurrentTaskDetails(id: string | undefined) {
    this.showTaskDetails = true;
    this.taskService.getTaskDetails(id).subscribe({
      next: (data: Task) => {
        this.currentTask = data;
      },
    });
  }

  CloseTaskDetails(closeForm: boolean) {
    this.showTaskDetails = closeForm;
  }

  CloseCreateTaskForm(closeForm: boolean) {
    this.showCreateTaskForm = closeForm;
  }

  CreateOrUpdateTask(data: Task) {
    if (!this.editMode) {
      this.taskService.CreateTask(data);
    } else {
      this.taskService.UpdateTask(this.currentTaskId, data);
    }
  }

  FetchAllTaskClicked() {
    this.fetchAllTasks();
  }

  private fetchAllTasks() {
    this.isLoading = true;
    this.taskService.GetAlltasks().subscribe({
      next: (tasks) => {
        this.allTasks = tasks;
        this.searchedTasks = tasks;
        this.isLoading = false;
      },
      error: (error) => {
        this.setErrorMessage(error);
        this.isLoading = false;
      },
    });
  }

  private setErrorMessage(err: HttpErrorResponse) {
    if (err.error.error === "Permission denied") {
      this.errorMessage = "You do not have permisssion to perform this action";
    } else {
      this.errorMessage = err.message;
    }

    setTimeout(() => {
      this.errorMessage = null;
    }, 3000);
  }

  DeleteTask(id: string | undefined) {
    this.taskService.DeleteTask(id);
    this.fetchAllTasks();
  }

  DeleteAllTask() {
    this.taskService.DeleteAllTasks();
    this.fetchAllTasks();
  }

  OnEditTaskClicked(id: string | undefined) {
    this.currentTaskId = id;

    //OPEN EDIT TASK FORM
    this.showCreateTaskForm = true;
    this.editMode = true;

    this.selectedTask = this.allTasks.find((task) => {
      return task.id === id;
    });
  }

  get tasksByStatus() {
    return this.searchedTasks.reduce((acc, task) => {
      if (!acc[task.status]) {
        acc[task.status] = [];
      }
      acc[task.status].push(task);
      return acc;
    }, {});
  }

  SearchTask(searchForm: NgForm) {
    this.searchQuery = searchForm.value.searchQuery.toLowerCase();

    if (searchForm.valid) {
      this.searchedTasks = this.allTasks.filter(
        (task) =>
          task.title.toLowerCase().includes(this.searchQuery) ||
          task.assignedTo.toLowerCase().includes(this.searchQuery)
      );
      console.log(this.searchedTasks);
    }
  }
}
<div class="dashboard-container">
  <div style="display: flex">
    <div style="width: 50%">
      <h2>Dashboard</h2>

      <form action="#" #searchForm="ngForm" (submit)="SearchTask(searchForm)">
        <input
          type="text"
          name="searchQuery"
          placeholder="Search for task..."
          ngModel
          required
        />
      </form>
    </div>
    <div style="width: 50%; text-align: right">
      <button class="create-task-btn" (click)="OpenCreateTaskForm()">
        Create Task
      </button>
      <button class="create-task-btn" (click)="FetchAllTaskClicked()">
        Fetch Tasks
      </button>
      <button class="create-task-btn" (click)="DeleteAllTask()">
        Clear Tasks
      </button>
    </div>
  </div>

  <hr />

  <div class="dashboard-item-container">
    <app-snackbar
      [errorMessage]="errorMessage"
      *ngIf="errorMessage"
    ></app-snackbar>
    <app-loader *ngIf="isLoading"></app-loader>

    <div style="text-align: center" *ngIf="filteredTasks.length < 1">
      <h2>No task has been created yet!</h2>
    </div>

    <ng-container *ngIf="!isLoading">
      <div
        class="task-status-container"
        *ngFor="let status of ['open', 'started', 'in progress', 'complete']"
      >
        <h3>{{ status | uppercase }}</h3>
        <div
          class="task-item"
          [ngClass]="'task-' + task.status"
          *ngFor="let task of tasksByStatus[status] || []"
        >
          <div class="task-title">
            <h3>{{ task.title }}</h3>
          </div>
          <p><b>Assigned to: </b>{{ task.assignedTo }}</p>
          <p><b>Priority: </b>{{ task.priority }}</p>
          <p><b>Status: </b>{{ task.status }}</p>
          <div class="task-action-icons">
            <button
              class="task-action-btn"
              title="Detail"
              (click)="showCurrentTaskDetails(task.id)"
            >
              <i class="fa fa-info-circle" aria-hidden="true"></i>
            </button>
            <button
              class="task-action-btn"
              title="Edit"
              (click)="OnEditTaskClicked(task.id)"
            >
              <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
            </button>
            <button
              class="task-action-btn"
              title="Delete"
              (click)="DeleteTask(task.id)"
            >
              <i class="fa fa-trash" aria-hidden="true"></i>
            </button>
          </div>
        </div>
      </div>
    </ng-container>
  </div>
</div>
